package orientacaoaobjeto;

public class JavaMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10 ; int b = 9;
		
		System.out.println("O número maior é : "+Math.max(a, b));
		System.out.println("O número maior é : "+Math.min(a, b));
		System.out.println("A raiz quadrada de 225 é : "+Math.sqrt(225));
		System.out.printf("A raiz quadrada de 255 é : %.2f",Math.sqrt(255));
		
	}

}
